import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Home from './components/Home';
import MusicPlayer from './components/MusicPlayer';
import Search from './components/Search';
import './App.css';
const corsAnywhere = require('cors-anywhere');
const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
const apiUrl = 'https://api.audiomack.com/v1/';

// Start the proxy server
corsAnywhere.createServer().listen(8080, () => {
  console.log(`Proxy server running on port ${8080}`);
});


function App() {

  const [featuredItems, setFeaturedItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [recentlyPlayedItems, setRecentlyPlayedItems] = useState([]);

  useEffect(() => {
    // Fetch list of recommended or popular songs, playlists, or albums
    fetch("https://api.audiomack.com/v1/music?filter=featured&limit=6")
      .then((response) => response.json())
      .then((data) => {
        setFeaturedItems(data.data);
      })
      .catch((error) => console.log(error));

    // Fetch list of music genres or moods
    fetch("https://api.audiomack.com/v1/genres?limit=6")
      .then((response) => response.json())
      .then((data) => {
        setCategories(data.data);
      })
      .catch((error) => console.log(error));

    // Fetch list of recently played songs or playlists
    fetch("https://api.audiomack.com/v1/me/recently-played?limit=6")
      .then((response) => response.json())
      .then((data) => {
        setRecentlyPlayedItems(data.data);
      })
      .catch((error) => console.log(error));
  }, []);
  return (
    <BrowserRouter>
      <div className="App">
        <header>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/search">Search</Link>
              </li>
              <li>
                <Link to="//music-player">Music Player</Link>
              </li>
            </ul>
          </nav>
        </header>
        <main>
          <Routes>
            <Route path="/" element={<Home featuredItems={featuredItems} categories={categories} recentlyPlayedItems={recentlyPlayedItems}/>} />
            <Route path="/music-player" element={<MusicPlayer />} />
            <Route path="/search" element={<Search />} />
          </Routes>
        </main>
        <footer>
          <p>&copy; 2023 Skio Music App</p>
        </footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
