import React, { useState, useEffect } from 'react';

function Home() {
  const [featuredItems, setFeaturedItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [recentlyPlayedItems, setRecentlyPlayedItems] = useState([]);

  useEffect(() => {
    // Fetch list of featured shows
    fetch("https://api.mixcloud.com/discover/featured/?limit=6")
      .then((response) => response.json())
      .then((data) => {
        setFeaturedItems(data.data);
      })
      .catch((error) => console.log(error));

    // Fetch list of categories
    fetch("https://api.mixcloud.com/categories/?limit=6")
      .then((response) => response.json())
      .then((data) => {
        setCategories(data.data);
      })
      .catch((error) => console.log(error));

    // Fetch list of recently played shows
    fetch("https://api.mixcloud.com/me/listens/?limit=6")
      .then((response) => response.json())
      .then((data) => {
        setRecentlyPlayedItems(data.data);
      })
      .catch((error) => console.log(error));
  }, []);

  return (
    <div className="home">
      <div className="featured">
        <h2>Featured Shows</h2>
        <div className="show-list">
          {featuredItems.map((item) => (
            <div key={item.key} className="show">
              <img src={item.pictures.large} alt={item.name} />
              <h3>{item.name}</h3>
              <p>{item.user.name}</p>
            </div>
          ))}
        </div>
      </div>
      <div className="categories">
        <h2>Categories</h2>
        <div className="category-list">
          {categories.map((category) => (
            <div key={category.key} className="category">
              <img src={category.pictures.large} alt={category.name} />
              <h3>{category.name}</h3>
            </div>
          ))}
        </div>
      </div>
      <div className="recently-played">
        <h2>Recently Played</h2>
        <div className="show-list">
          {recentlyPlayedItems.map((item) => (
            <div key={item.key} className="show">
              <img src={item.show.pictures.large} alt={item.show.name} />
              <h3>{item.show.name}</h3>
              <p>{item.show.user.name}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
