import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Home from './components/Home';
import MusicPlayer from './components/MusicPlayer';
import Search from './components/Search';
import './App.css';

function App() {
  const [featuredItems, setFeaturedItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [recentlyPlayedItems, setRecentlyPlayedItems] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch list of recommended or popular songs, playlists, or albums
        const response1 = await fetch(
          'https://api.audiomack.com/v1/music?filter=featured&limit=6'
        );
        const data1 = await response1.json();
        setFeaturedItems(data1.data);

        // Fetch list of music genres or moods
        const response2 = await fetch(
          'https://api.audiomack.com/v1/genres?limit=6'
        );
        const data2 = await response2.json();
        setCategories(data2.data);

        // Fetch list of recently played songs or playlists
        const response3 = await fetch(
          'https://api.audiomack.com/v1/me/recently-played?limit=6'
        );
        const data3 = await response3.json();
        setRecentlyPlayedItems(data3.data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  return (
    <BrowserRouter>
      <div className="App">
        <header>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/search">Search</Link>
              </li>
              <li>
                <Link to="/music-player">Music Player</Link>
              </li>
            </ul>
          </nav>
        </header>
        <main>
          <Routes>
            <Route path="/" element={<Home featuredItems={featuredItems}/>} />
            <Route path="/music-player" element={<MusicPlayer />} />
            <Route path="/search" element={<Search />} />
          </Routes>
        </main>
        <footer>
          <p>&copy; 2023 Skio Music App</p>
        </footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
